import { getTypes, addPokemon } from "../../actions";


export default function Create() {

let dispatch = useDispatch();
//let types = useSelector((state) => state.types);
let types = [];

const [input, setInput] = useState( {
    name: "", 
    //hp: "", 
    // attack: "",
    // defense: "",
    // speed: "",
    // height: "",
    // weight: "",
    // types: [],
})

function handleChange(e) {

    setInput( {
         ...input,
        [e.target.name]: e.target.value,
    //...input,
//[e.target.name]: "231231",


})

}

const ejemplo = {
    name: "12", 
    hp: "10", 
    attack: "10",
    defense: "34",
    speed: "8",
    height: "78",
    weight: "234",
    types: [ "fire", "poison" ]
    }

function handleSubmit(e) {

   e.preventDefault();

    console.log(ejemplo);
    console.log(input);

    dispatch(addPokemon(input));
    setInput({
        name: "", 
        // hp: "", 
        // attack: "",
        // defense: "",
        // speed: "",
        // height: "",
        // weight: "",
        // types: [],
    })
}



useEffect(() =>{
    dispatch(getTypes());
}, []) 

    return (
        <form>
            <div>Create Your Pokemon!</div>
            <div>
                <label>Name:</label>
                <input type="text" name="name" onChange={(e) => {handleChange(e)}}/>
            </div>
            {/* <div>
                <label>HP:</label>
