            <div>
                <select>
                    {
                    types.map(t => {
                        return (
                            <option key={t.id}>{t.name}</option>
                        )
                    })
                }
                    </select>


                {
                    types.map(t => {
                        return (
                        <div key={t.id}>
                        <p>{t.name}</p>
                        <button>X</button>
                        </div>
                        )
                    })
                }
            </div>
            <button onClick={handleSubmit}>CREATE POKEMON</button> 
