import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import { useEffect } from "react";
import { getTypes, addPokemon } from "../../actions";
import './create.css';
import { Link } from "react-router-dom";

export default function Create() {

    let dispatch = useDispatch();
    let types = useSelector((state) => state.types);

    const [input, setInput] = useState({

        name: "",
        image: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        types: []

    })

    const [errors, setErrors] = useState({
        name: "",
        image: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        types: "",
    });


    function validate(input) {
        let errors = {};
        console.log(input)

        if (!input.name) {
          errors.name = 'Name is required.';
        } else if (!/^.{0,40}$/.test(input.name)) {
          errors.name = 'Name is too long.';
        }

        if (!input.summary) {
            errors.summary = 'Summary is required.';
          } else if (!/^.{0,40}$/.test(input.summary)) {
            errors.summary = 'Summary is too long.';
          }

        if (!input.healthScore) {
            errors.healthScore = 'Health Score is required.';
          } else if (!/^(0*[1-9][0-9]*(\.[0-9]+)?|0+\.[0-9]*[1-9][0-9]*)$/gm.test(input.healthScore)) {
            errors.healthScore = 'Health Score must be number bigger than 0.';
          }

        return errors;
    }

    function handleChange(e) {

        setInput({
            ...input,
            [e.target.name]: e.target.value,
        })

        validate();
        console.log(errors);

    }


    function handleSubmit(e) {

        e.preventDefault();
        dispatch(addPokemon(input));
        console.log(input);
        setInput({
            name: "",
            image: "",
            hp: "",
            attack: "",
            defense: "",
            speed: "",
            height: "",
            weight: "",
            types: [],
        })

        alert("Pokemon Creado!")
    }

    function handleSelect(e) {
        e.preventDefault();
        setInput({

            ...input,
            types: [...input.types, e.target.value]

        })

        console.log(input.types)

    }

    function handleClose(type) {
        setInput({

            ...input,
            types: input.types.filter(t => t !== type)

        })
    }


    useEffect(() => {
        dispatch(getTypes());
    }, [dispatch])

    return (
        <div className='background'>

            <div className='container'>

                <Link to='/home'>
                    <button className="btn-back">BACK</button>
                </Link>

                <div className="create">Create Your Pokemon!</div>

                <form className="form" onSubmit={handleSubmit}>
                    <div>
                        <label className="label">Name:</label>
                        <input type="text" name="name" onChange={(e) => { handleChange(e) }} />
                        { errors.name? <p>{errors.name}</p> : <p>Nada</p>  }

                    </div>
                    <div>
                        <label className="label">Image:</label>
                        <input type="text" name="image" onChange={(e) => { handleChange(e) }}/>
                        { errors.name? <p>{errors.name}</p> : <p></p>  }
                        </div>
                    <div>
                        <label className="label">HP:</label>
                        <input type="text" name="hp" onChange={(e) => { handleChange(e) }}/>
                    </div>
                    <div>
                        <label className="label">Attack:</label>
                        <input type="text" name="attack" onChange={(e) => { handleChange(e) }}/>
                    </div>
                    <div>
                        <label className="label">Defense:</label>
                        <input type="text" name="defense" onChange={(e) => { handleChange(e) }}/>
                    </div>
                    <div>
                        <label className="label">Speed:</label>
                        <input type="text" name="speed" onChange={(e) => { handleChange(e) }}/>
                    </div>
                    <div>
                        <label className="label">Weight:</label>
                        <input type="text" name="weight" onChange={(e) => { handleChange(e) }}/>
                    </div>
                    <div>
                        <label className="label">Height:</label>
                        <input type="text" name="height" onChange={(e) => { handleChange(e) }}/>
                    </div>

                    <div>
                        <select onChange={(e) => { handleSelect(e) }}>
                            {
                                types.map(t => {
                                    return (
                                        <option value={t.name} key={t.id}>{t.name}</option>
                                    )
                                })
                            }
                        </select>

<div>
                        {
                    input.types.map(t => 
                            <div key={t.id}>
                                <p>{t}</p>
                                <button onClick={() => handleClose(t)} >X</button>

                            </div>

                    )
                }
                </div>
                    </div>
                    <button className='btn-create-pokemon' type="Submit">CREATE POKEMON</button>
                </form>
            </div>
        </div>
    )

}
