import { GET_POKEMONS } from '../actions';

const state = {
    allPokemons: [],
}


function reducer(state, action) {

if(action.type === GET_POKEMONS) {
    return {
        ...state,
        allPokemons: action.payload,
    }
}

};

export default reducer;
