import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import { useEffect } from "react";
import { getTypes, addPokemon } from "../../actions";
import './create.css';
import { Link } from "react-router-dom";

export default function Create() {

    let dispatch = useDispatch();
    let types = useSelector((state) => state.types);
    //let types = [];

    const [input, setInput] = useState({

        name: "",
        image: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        types: []

    })

    function handleChange(e) {

        setInput({
            ...input,
            [e.target.name]: e.target.value,
        })

    }

    const ejemplo = {
        name: "PIKA",
        hp: "10",
        attack: "10",
        defense: "34",
        speed: "8",
        height: "78",
        weight: "234",
        types: ["fire", "poison"]
    }

    function handleSubmit(e) {

        e.preventDefault();

        console.log(ejemplo);

        dispatch(addPokemon(input));
        setInput({
            name: "",
            image: "",
            hp: "",
            attack: "",
            defense: "",
            speed: "",
            height: "",
            weight: "",
            types: [],
        })

        alert("Pokemon Creado!")
        console.log(input);
    }


    useEffect(() => {
        dispatch(getTypes());
    }, [dispatch])

    return (
        <div className='background'>

            <div className='container'>

                <Link to='/home'>
                    <button className="btn-back">BACK</button>
                </Link>

                <div className="create">Create Your Pokemon!</div>

                <form className="form" onSubmit={handleSubmit}>
                    <div>
                        <label className="label">Name:</label>
                        <input type="text" name="name" onChange={(e) => { handleChange(e) }} />
                    </div>
                    <div>
                        <label className="label">Image:</label>
                        <input type="text" name="image" />
                    </div>
                    <div>
                        <label className="label">HP:</label>
                        <input type="text" name="hp" />
                    </div>
                    <div>
                        <label className="label">Attack:</label>
                        <input type="text" name="attack" />
                    </div>
                    <div>
                        <label className="label">Defense:</label>
                        <input type="text" name="defense" />
                    </div>
                    <div>
                        <label className="label">Speed:</label>
                        <input type="text" name="speed" />
                    </div>
                    <div>
                        <label className="label">Weight:</label>
                        <input type="text" name="weight" />
                    </div>
                    <div>
                        <label className="label">Height:</label>
                        <input type="text" name="height" />
                    </div>

                    <div>
                        <select>
                            {
                                types.map(t => {
                                    return (
                                        <option key={t.id}>{t.name}</option>
                                    )
                                })
                            }
                        </select>


                        {/* {
                    types.map(t => {
                        return (
                            <div key={t.id}>
                                <p>{t.name}</p>
                                <button>X</button>
                            </div>
                        )
                    })
                } */}
                    </div>
                    <button className='btn-create-pokemon' type="Submit">CREATE POKEMON</button>
                </form>
            </div>
        </div>
    )

}
