import { GET_POKEMONS } from '../actions';

const state = {
    allPokemons: [],
};


function reducer(state, action) {

if(action.type === GET_POKEMONS) {
    console.log("Entra en el reducer.")
    return {
        ...state,
        allPokemons: action.payload,
    }
}

return state;

};

export default reducer;
